package aug14;

public class Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 25;
		age++;
		System.out.println(age);
		System.out.println(age++);
		System.out.println(age);
////////////
		int rollno = 100;
		++rollno;
		System.out.println(rollno);
		System.out.println(++rollno);
		System.out.println(rollno + rollno);
		//////////

		int marks = 200;

		--marks;
		System.out.println(marks);
		System.out.println(--marks);
		System.out.println(--marks);
		marks--;
		System.out.println(marks);
		System.out.println(marks--);
		System.out.println(marks);

	}
}
