package aug21;

public class Whileloopex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		while (i <= 10) {
			System.out.println(i);
			i++;
		}
		int x = 1;
		while (x <= 10) {
			System.out.println(x + " welcome to whileloop program");
			x++;

		}
		int s = 0;
		while (s <= 5) {
			System.out.println(s + " hello world");
			s++;

		}

		int a = 1;
		while (a <= 10) {
			System.out.println(a++ + " a for apple");
			a++;
		}

		int z=10;
		while(z>=0) {
			System.out.println(z + " hi");
			z--;
		}
		
	}
}
